mapper.update:
UPDATE tbl_user
SET name = #{member.name}, room_code = #{roomCode}, birth = #{member.birth}, email = #{member.email}
WHERE id = #{member.id}

mapper.select:
SELECT *, room_code as roomCode, admin_yn as adminYn, insert_dt as insertDt, update_dt as updateDt FROM tbl_user
(id LIKE CONCAT('%', #{keyword}, '%') OR name LIKE CONCAT('%', #{keyword}, '%') OR room_code LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY insert_dt LIMIT #{offset}, 10;
